insert into book (year, isbn, description, auth_id, image_id, title, gender_id, stock) values ('2002','6129583074','Alba viaja alrededor del mundo visitando varios países donde encontrará nuevos amigos y descubrirá nuevas comidas, animales, paisajes y monumentos y hasta diferentes razas. ¿Quieres conocer Kenia, Rusia, China, Estados Unidos y much,1os más países de la mano de Alba?', '3','4', 'Cuentos de otoño', 3,1);
