insert into book (year, isbn, description, auth_id, image_id, title, gender_id, stock) values ('2019','5978468967','Su protagonista, conocido solamente como K., lucha para acceder a las misteriosas autoridades de un castillo que gobierna el pueblo al cual K ha llegado a trabajar como agrimensor. En líneas generales, trata sobre la alienación, la burocracia, y la frustración, aparentemente interminable, de lo,1s intentos de un hombre por incorporarse al sistema.', '1', '48', 'La reina del castillo oscuro', 4, 1);
insert into book (year, isbn, description, auth_id, image_id, title, gender_id, stock) values ('2013','8689393485','El gran renovador de la literatura de terror en el siglo XX se inclinó en esta temprana ficción por el clásico cuento gótico al estilo de Le Fanu y Lewis, lejos de su habitual y reconocible \"horror cósmico\", presente en los mitos de Cthulhu.', '36', '49', 'Alquimia', 4 ,1);
